import {
    ADD_TASK,
    EDIT_TASK ,
    REMOVE_TASK,
    HANDLE_INPUT,
    DISPLAY_ITEM ,
    SUBMIT
} from '../actions/actions'

export const reducer = (state, action) => {
    if(action.type === HANDLE_INPUT){
        const {task} = action.payload;
        return {
            ...state,
            [action.name]: task
        }
    }
}
