import React, {useState} from "react";


const List = ({list, setList, handleEdit, task, isEditing, checked, removeTask, handleCheck, editItem, handleSubmit, handleChange}) => {
    const [search, setSearch] = useState("")
    const [itemFilter, setItemFilter] = useState([])

    const handleRemove = () => {
        setList([])
        handleCheck()
    }

    const handleSearch = (e) => {
        const value = e.target.value;
        setSearch(value)
        if(search !== ''){
            const filterItem = list.filter((item) => {
                return Object.values(item).join('').toLowerCase().includes(value.toLowerCase())
            })
            setItemFilter(filterItem)
        }else {
            return list
        }

    }



    const updateTask = (id) => {
        const newTask = list.find((item) => {
            console.log(item)
            if(item.id === id){
                const updateTask = {
                    ...item,
                    task
                }
                console.log(updateTask)
                 return updateTask

            }
            return item
        })
        console.log(newTask)
        setList(newTask)
    }


    return (
        <>
            <section className="list-section">
                 <div className='list-container'>
                         <h4>To Do List</h4>
                    <div className='form-control'>
                        <input type="search" className='search input' placeholder='Search...' onChange={handleSearch} value={search}/>
                    </div>
            {
                search.length > 1 ? (
                    itemFilter.map((item, index) => {
                        const {id, title, description, priority} = item
                        return (
                            <>
                                <article className="task-list" key={index}>
                                    <div className="wrapper-check">
                                        <input type='checkbox' defaultChecked={checked} onClick={handleCheck} style={{marginRight: '10px'}}/>
                                        <span>{title}</span>
                                    </div>
                                    <div className='wrapper-btn'>
                                        <button className='btn btn-detail' onClick={() => handleEdit(index, id)}>Detail</button>
                                        <button className='btn btn-remove' onClick={() => removeTask(id)}>Remove</button>
                                    </div>
                                </article>
                                {
                                    isEditing === index ?
                                        (<div className='form-update'>
                                            <form onSubmit={handleSubmit}>
                                                <input type="text" value={task.title} name="title" onChange={handleChange} className='input'/>
                                                <div className='description'>
                                                    <span>Description</span>
                                                    <textarea value={description} name="description" >
                                                </textarea>
                                                </div>
                                                <div className='options'>
                                                    <div className='date'>
                                                        <label>Due Date</label>
                                                        <input type="date" name="date" className='input input-date'/>
                                                    </div>
                                                    <div className='priority'>
                                                        <label>Priority</label>
                                                        <select name='priority' className='input input-date'>{priority}
                                                            <option value="Low">Low</option>
                                                            <option value='Normal'>Normal</option>
                                                            <option value='High'>High</option>
                                                        </select>
                                                    </div>
                                                </div>
                                                <button onClick={() => handleSubmit(id)} className='btn btn-add'>edit</button>
                                            </form>

                                        </div>) : false
                                }
                            </>
                        )
                    })
                ) :
                ( list.map((item, index) => {
                    const {id, title, description, priority} = item
                    return (
                        <>
                            <article className="task-list" key={index}>
                                <div className="wrapper-check">
                                    <input type='checkbox' defaultChecked={checked} onClick={handleCheck} style={{marginRight: '10px'}}/>
                                    <span>{title}</span>
                                </div>
                                <div className='wrapper-btn'>
                                    <button className='btn btn-detail' onClick={() => handleEdit(index)}>Detail</button>
                                    <button className='btn btn-remove' onClick={() => removeTask(id)}>Remove</button>
                                </div>
                            </article>
                            {
                                isEditing === index ?
                                    (<div className='form-update'>
                                        <form onSubmit={handleSubmit}>
                                            <input type="text" value={task.title} name="title" onChange={handleChange} className='input'/>
                                            <div className='description'>
                                                <span>Description</span>
                                                <textarea value={description} name="description" >
                                                </textarea>
                                            </div>
                                            <div className='options'>
                                                <div className='date'>
                                                    <label>Due Date</label>
                                                    <input type="text" className='input input-date'/>
                                                </div>
                                                <div className='priority'>
                                                    <label>Priority</label>
                                                    <select name='priority' className='input input-date'>{priority}
                                                        <option value="Low">Low</option>
                                                        <option value='Normal'>Normal</option>
                                                        <option value='High'>High</option>
                                                    </select>
                                                </div>
                                            </div>
                                           <button onClick={() => updateTask(id)} className='btn btn-add'>edit</button>
                                        </form>

                                    </div>) : false
                            }
                        </>

                    )
                })
                )
            }
                 </div>
                  {
                    checked ? <div className='bulk-action'>
                        <span>Bulk action:</span>
                        <div className='wrapper-btn'>
                            <button className='btn btn-done'>Submit</button>
                            <button className='btn btn-remove' onClick={handleRemove}>Remove</button>
                        </div>
                    </div>: null
                }
            </section>
        </>
    );
}

export default List;
