import React, {useState, useEffect, useContext} from "react";
import List from './Components/List';
import Add from './Components/Add'
const getLocalStorage = () => {
    let list = localStorage.getItem('list');
    if(list){
        return ( list = JSON.parse(localStorage.getItem('list')))
    }else {
        return []
    }
}



function App() {

    const [list, setList] = useState(getLocalStorage());
    const [isEditing, setIsEditing] = useState(false);
    const [checked, setChecked] = useState(false)
    const [editId, setEditID] = useState(null)
    const [task, setTask] = useState({title: "", description: "", date: "", priority: "Normal"});

    const handleChange = (e) => {
        e.preventDefault()
        const value = e.target.value;
        const name = e.target.name;
        setTask({...task, [name]: value})
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        if(!task.title){
            alert("please enter title")
        }else if (task.title && isEditing){
            setList(
                list.map((item) => {
                    if(item.id === editId){
                        return {...item, task}
                    }
                    return item
                })
            )
            setTask({title: '', priority: ''})
            setEditID(null)
            setIsEditing(false)
            alert("success")
        } else {
            const newItem = {id: new Date().getTime().toString(), ...task}
            setList([...list, newItem])
            setTask({title: '', priority: ''})
        }
    }

    const handleEdit = (index) => {
        if(isEditing === index) {
            return setIsEditing(null)
        }
        setIsEditing(index)
    }

    const removeTask = (id) => {
        setList(list.filter((item) => item.id !== id))
    }

    const handleCheck = () => {
        setChecked(!checked)
    }

    useEffect(() => {
        localStorage.setItem('list', JSON.stringify(list))
    }, [list])

    const editItem = (id) => {
        const specificItem = list.find((item) => item.id === id);
        console.log(specificItem)
        setIsEditing(true)
        setEditID(id)
        setTask(specificItem.title)
    }



    return (
        <>
            <div className='container'>
                <main className='section-center'>
                    <Add handleChange={handleChange} handleSubmit={handleSubmit} task={task}/>
                    <List task={task}
                          list={list}
                          setList={setList}
                          isEditing={isEditing}
                          checked={checked}
                          handleSubmit={handleSubmit}
                          handleChange={handleChange}
                          editItem={editItem}
                          handleEdit={handleEdit}
                          removeTask={removeTask}
                          handleCheck={handleCheck}/>
                </main>
            </div>
        </>
    )

}

export default App;
