import React, {useState, useEffect, useContext} from "react";
import List from './Components/List';
import Add from './Components/Add'
const getLocalStorage = () => {
    let list = localStorage.getItem('list');
    console.log(list)
    if(list){
        return ( list = JSON.parse(localStorage.getItem('list')))
    }else {
        return []
    }
}



function App() {

    const [list, setList] = useState(getLocalStorage());
    const [isEditing, setIsEditing] = useState(false);
    const [checked, setChecked] = useState(false)
    const [editId, setEditID] = useState(null)
    const [task, setTask] = useState({title: "", description: "", priority: ""});

    const handleChange = (e) => {
        e.preventDefault()
        const value = e.target.value;
        const name = e.target.name;
        setTask({...task, [name]: value})
        console.log(task)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        if(!task.title){
            alert("please enter title")
        }else if (task.title && isEditing){
            setList(
                list.map((item) => {
                    if(item.id === editId){
                        return {...item, task}
                    }
                    return item
                })
            )
            setTask({title: '', priority: ''})
            setEditID(null)
            setIsEditing(false)
            alert("success")
        } else {
            const newItem = {id: new Date().getTime().toString(), ...task}
            setList([...list, newItem])
            setTask({title: '', priority: ''})
        }
    }

    const handleEdit = (index) => {
        if(isEditing === index) {
            return setIsEditing(null)
        }
        setIsEditing(index)
    }

    const removeTask = (id) => {
        setList(list.filter((item) => item.id !== id))
    }

    const handleCheck = () => {
        setChecked(checked)
    }

    return (
        <>
            <main className='section-center'>
                <Add handleChange={handleChange} handleSubmit={handleSubmit} task={task}/>
                <List list={list} isEditing={isEditing} handleEdit={handleEdit} removeTask={removeTask}/>
            </main>
        </>
    )

}

export default App;
