import React, {useState} from 'react';

const getLocalStorage = () => {
    let list = localStorage.getItem('list');
    if(list){
        return (list = JSON.parse(localStorage.getItem('list')))
    }else {
        return []
    }
}


const Add = (props) => {

        const [list, setList] = useState(getLocalStorage());
    const [isEditing, setIsEditing] = useState(false);
    const [editId, setEditID] = useState(null)
    const [task, setTask] = useState({title: "", priority: ""});

    const handleChange = (e) => {
        e.preventDefault()
        const value = e.target.value;
        const name = e.target.name;
        setTask({...task, [name]: value})
        console.log(task)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        if(!task.title){
            alert("please enter title")
        }else if (task.title && isEditing){
            setList(
                list.map((item) => {
                    if(item.id === editId){
                        return {...item, title: name}
                    }
                    return item
                })
            )
        };
        setTask({title: '', priority: ''})
        setEditID(null)
        setIsEditing(false)
        alert("success")

        const newItem = {id: new Date().getTime().toString(), title: name}
        setList([...list, newItem])

    }

    return (
        <>
            <section className='add-form'>
                <header>
                    <h4>Add new task</h4>
                </header>
                <form>
                    <input type="text" value={task.title} name="title" onChange={handleChange}/>
                    <div className='description'>
                        <span>Description</span>
                        <textarea>
                        </textarea>
                    </div>
                    <div className=''>
                        <div className='date'>
                            <label>Due Date</label>
                            <input type="text" onChange={handleChange}/>
                        </div>
                        <div className='priority'>
                            <label>Priority</label>
                            <input type="text" onChange={handleChange} value={task.priority} name='priority'/>
                        </div>
                    </div>
                    <button onClick={handleSubmit}>Add</button>
                </form>
            </section>
        </>
    );
}

export default Add;
