import React, {useState} from "react";


const List = ({list, setList, handleEdit, isEditing, removeTask}) => {
    const [search, setSearch] = useState("")
    const [checked, setChecked] = useState(false)
    const [complete, setComplete] = useState(false)
    const [itemFilter, setItemFilter] = useState([])
    const [newTask, setNewTask] = useState({title: "", description: "", date: new Date(), priority: "Normal"})


    const handleRemove = () => {
        setList([])
        handleCheck()
    }

    const handleSearch = (e) => {
        const value = e.target.value;
        setSearch(value)
        if(search !== ''){
            const filterItem = list.filter((item) => {
                return Object.values(item).join('').toLowerCase().includes(value.toLowerCase())
            })
            setItemFilter(filterItem)
        }else {
            return list
        }

    }

    const handleCheck = (item) => {
        setChecked(!checked)
        handleDone(item)
    }

    const changeUpdate = (e) => {
        const value = e.target.value;
        const name = e.target.name;
        setNewTask({...newTask, [name]: value})
    }

    const updateTask = (id) => {
      setList(
          list.map((item) => {
              if(item.id === id){
                  return {...item, title: newTask.title, description: newTask.description, date: newTask.date, priority: newTask.priority, isComplete: true}
              }
              return item
          })
      )
        setNewTask({title: "", description: "", date: "", priority: "Normal", isComplete: false})
    }

    const handleDone = (list) => {
        console.log(isComplete)
        const isComplete = list.isComplete
        setTask({...list, isComplete: isComplete})
    }



    return (
        <>
            <section className="list-section">
                 <div className='list-container'>
                         <h4>To Do List</h4>
                    <div className='form-control'>
                        <input type="search" className='search input' placeholder='Search...' onChange={handleSearch} value={search}/>
                    </div>
            {
                search.length > 1 ? (
                    itemFilter.map((item, index) => {
                        const {id, title, description, priority, date} = item
                        return (
                            <>
                                <article className="task-list" key={index}>
                                    <div className="wrapper-check">
                                        <input type='checkbox' defaultChecked={checked} onClick={() => handleCheck(item)} style={{marginRight: '10px'}}/>
                                        <span>{title}</span>
                                    </div>
                                    <div className='wrapper-btn'>
                                        <button className='btn btn-detail' onClick={() => handleEdit(index)}>Detail</button>
                                        <button className='btn btn-remove' onClick={() => removeTask(id)}>Remove</button>
                                    </div>
                                </article>
                                {
                                    isEditing === index ?
                                        (<div className='form-update'>
                                            <form onSubmit={e => e.preventDefault()}>
                                                <input type="text" placeholder={title} value={newTask.title} name="title" onChange={changeUpdate} className='input'/>
                                                <div className='description'>
                                                    <span>Description</span>
                                                    <textarea placeholder={description} name="description" value={newTask.description} onChange={changeUpdate} />
                                                </div>
                                                <div className='options'>
                                                    <div className='date'>
                                                        <label>Due Date</label>
                                                        <input type="date" name="date" value={newTask.date} placeholder={date} onChange={changeUpdate} className='input input-date'/>
                                                    </div>
                                                    <div className='priority'>
                                                        <label>Priority</label>
                                                        <select name='priority' className='input input-date' placeholder={priority} onChange={changeUpdate} value={newTask.priority}>
                                                            <option value="Low">Low</option>
                                                            <option value='Normal'>Normal</option>
                                                            <option value='High'>High</option>
                                                        </select>
                                                    </div>
                                                </div>
                                                <button onClick={() => updateTask(id)} className='btn btn-add'>Update</button>
                                            </form>
                                        </div>) : false
                                }
                            </>
                        )
                    })
                ) :
                ( list.map((item, index) => {
                    const {id, title, description, priority, date} = item
                    return (
                        <>
                            <article className="task-list" key={index}>
                                <div className="wrapper-check">
                                    <input type='checkbox' defaultChecked={checked} onClick={handleCheck} style={{marginRight: '10px'}}/>
                                    <span>{title}</span>
                                </div>
                                <div className='wrapper-btn'>
                                    <button className='btn btn-detail' onClick={() => handleEdit(index, id)}>Detail</button>
                                    <button className='btn btn-remove' onClick={() => removeTask(id)}>Remove</button>
                                </div>
                            </article>
                            {
                                isEditing === index ?
                                    (<div className='form-update'>
                                        <form onSubmit={e => e.preventDefault()}>
                                            <input type="text" placeholder={title} value={newTask.title} name="title" onChange={changeUpdate} className='input'/>
                                            <div className='description'>
                                                <span>Description</span>
                                                <textarea placeholder={description} name="description" value={newTask.description} onChange={changeUpdate} />
                                            </div>
                                            <div className='options'>
                                                <div className='date'>
                                                    <label>Due Date</label>
                                                    <input type="date" name="date" value={newTask.date} placeholder={date} onChange={changeUpdate} className='input input-date'/>
                                                </div>
                                                <div className='priority'>
                                                    <label>Priority</label>
                                                    <select name='priority' className='input input-date' placeholder={priority} onChange={changeUpdate} value={newTask.priority}>
                                                        <option value="Low">Low</option>
                                                        <option value='Normal'>Normal</option>
                                                        <option value='High'>High</option>
                                                    </select>
                                                </div>
                                            </div>
                                           <button onClick={() => updateTask(id)} className='btn btn-add'>Update</button>
                                        </form>

                                    </div>) : false
                            }
                        </>

                    )
                })
                )
            }
                 </div>
                  {
                    checked && (<div className='bulk-action'>
                        <span>Bulk action:</span>
                        <div className='wrapper-btn'>
                            <button className='btn btn-done' onClick={handleDone}>Done</button>
                            <button className='btn btn-remove' onClick={handleRemove}>Remove</button>
                        </div>
                    </div>)
                }
            </section>
        </>
    );
}

export default List;
