import React from "react";


const List = ({list, handleEdit, isEditing, checked, removeTask, handleCheck, editItem}) => {

    return (
        <>
            <section>
                     <h4>To Do List</h4>
                    <div className='form-control'>
                        <input type="search" className='search' placeholder='Search...' />
                    </div>
            {
                list.map((item, index) => {
                    const {id, title, description, priority} = item
                    return (
                        <>
                            <article className="list-container" key={index}>
                                <div className="wrapper-check">
                                    <input type='checkbox' defaultChecked={checked} onClick={handleCheck} />
                                    <span>{title}</span>
                                </div>
                                <div className='wrapper-btn'>
                                    <button onClick={() => handleEdit(index)}>Detail</button>
                                    <button onClick={() => removeTask(id)}>Remove</button>
                                </div>
                            </article>
                            {
                                isEditing === index ?
                                    (<div>
                                        <input>{title}</input>
                                        <input type="text" value={description}>{description}</input>
                                        <input type="text" value="Due Date">Due Date</input>
                                        <input type="text" value={priority}>{priority}</input>
                                        <button onClick={() => editItem(id)}>edit</button>
                                    </div>) : false
                            }
                        </>

                    )
                })
            }
                {
                    checked ? <button>Submit</button> : null
                }
            </section>
        </>
    );
}

export default List;
