import React, {useState} from "react";


const List = ({list, setList, handleEdit, task, setTask, isEditing, checked, removeTask, handleCheck, handleSubmit, handleChange}) => {
    const [search, setSearch] = useState("")
    const [itemFilter, setItemFilter] = useState([])
    const handleRemove = () => {
        setList([])
        handleCheck()
    }

    const handleSearch = (e) => {
        const value = e.target.value;
        setSearch(value)
        if(search !== ''){
            const filterItem = list.filter((item) => {
                return Object.values(item).join('').toLowerCase().includes(value.toLowerCase())
            })
            setItemFilter(filterItem)
        }else {
            return list
        }

    }

    const changeEdit = (e, id) => {
       const item = list.map((item) => {
           if(item.id === id){
               return item
           }
       })
        const value = e.target.value
        const name = e.target.name
        console.log({...item, [name]: value})
        setTask({...item, [name]: value})
    }


    const updateTask = (id) => {
      setList(
          list.map((item) => {
              if(item.id === id){
                  return {...item, title: task.title}
              }
              return item
          })
      )
        setTask({title: "", description: "", date: "", priority: "Normal"})
    }

    return (
        <>
            <section className="list-section">
                 <div className='list-container'>
                         <h4>To Do List</h4>
                    <div className='form-control'>
                        <input type="search" className='search input' placeholder='Search...' onChange={handleSearch} value={search}/>
                    </div>
            {
                search.length > 1 ? (
                    itemFilter.map((item, index) => {
                        const {id, title, description, priority} = item
                        return (
                            <>
                                <article className="task-list" key={index}>
                                    <div className="wrapper-check">
                                        <input type='checkbox' defaultChecked={checked} onClick={handleCheck} style={{marginRight: '10px'}}/>
                                        <span>{title}</span>
                                    </div>
                                    <div className='wrapper-btn'>
                                        <button className='btn btn-detail' onClick={() => handleEdit(index, id)}>Detail</button>
                                        <button className='btn btn-remove' onClick={() => removeTask(id)}>Remove</button>
                                    </div>
                                </article>
                                {
                                    isEditing === index ?
                                        (<div className='form-update'>
                                            <form onSubmit={handleSubmit}>
                                                <input type="text" value={task.title} name="title" onChange={handleChange} className='input'/>
                                                <div className='description'>
                                                    <span>Description</span>
                                                    <textarea value={description} name="description" >
                                                </textarea>
                                                </div>
                                                <div className='options'>
                                                    <div className='date'>
                                                        <label>Due Date</label>
                                                        <input type="date" name="date" className='input input-date'/>
                                                    </div>
                                                    <div className='priority'>
                                                        <label>Priority</label>
                                                        <select name='priority' className='input input-date'>{priority}
                                                            <option value="Low">Low</option>
                                                            <option value='Normal'>Normal</option>
                                                            <option value='High'>High</option>
                                                        </select>
                                                    </div>
                                                </div>
                                                    <button onClick={() => updateTask(id)} className='btn btn-add'>Update</button>
                                            </form>

                                        </div>) : false
                                }
                            </>
                        )
                    })
                ) :
                ( list.map((item, index) => {
                    const {id, title, description, priority, date} = item
                    return (
                        <>
                            <article className="task-list" key={index}>
                                <div className="wrapper-check">
                                    <input type='checkbox' defaultChecked={checked} onClick={handleCheck} style={{marginRight: '10px'}}/>
                                    <span>{title}</span>
                                </div>
                                <div className='wrapper-btn'>
                                    <button className='btn btn-detail' onClick={() => handleEdit(index, id)}>Detail</button>
                                    <button className='btn btn-remove' onClick={() => removeTask(id)}>Remove</button>
                                </div>
                            </article>
                            {
                                isEditing === index ?
                                    (<div className='form-update'>
                                        <form onSubmit={handleSubmit}>
                                            <input type="text" placeholder={title} value={item.title} name="title" onChange={(e) => changeEdit(e, id)} className='input'/>
                                            <div className='description'>
                                                <span>Description</span>
                                                <textarea placeholder={description} name="description" >
                                                </textarea>
                                            </div>
                                            <div className='options'>
                                                <div className='date'>
                                                    <label>Due Date</label>
                                                    <input type="date" name="date" placeholder={date} onChange={handleChange} className='input input-date'/>
                                                </div>
                                                <div className='priority'>
                                                    <label>Priority</label>
                                                    <select name='priority' className='input input-date' placeholder={priority}>
                                                        <option value="Low">Low</option>
                                                        <option value='Normal'>Normal</option>
                                                        <option value='High'>High</option>
                                                    </select>
                                                </div>
                                            </div>
                                           <button onClick={() => updateTask(id)} className='btn btn-add'>Update</button>
                                        </form>

                                    </div>) : false
                            }
                        </>

                    )
                })
                )
            }
                 </div>
                  {
                    checked ? <div className='bulk-action'>
                        <span>Bulk action:</span>
                        <div className='wrapper-btn'>
                            <button className='btn btn-done'>Submit</button>
                            <button className='btn btn-remove' onClick={handleRemove}>Remove</button>
                        </div>
                    </div>: null
                }
            </section>
        </>
    );
}

export default List;
