import React, {useState, useEffect, useContext} from "react";
import List from './Components/List';
import Add from './Components/Add'
const getLocalStorage = () => {
    let list = localStorage.getItem('list');
    if(list){
        return (list = JSON.parse(localStorage.getItem('list')))
    }else {
        return []
    }
}



function App() {

    const [list, setList] = useState(getLocalStorage());
    const [isEditing, setIsEditing] = useState(false);
    const [editId, setEditID] = useState(null)
    const [task, setTask] = useState({title: "", priority: ""});

    const handleChange = (e) => {
        e.preventDefault()
        const value = e.target.value;
        const name = e.target.name;
        setTask({...task, [name]: value})
        console.log(task)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        if(!task.title){
            alert("please enter title")
        }else if (task.title && isEditing){
            setList(
                list.map((item) => {
                    if(item.id === editId){
                        return {...item, task}
                    }
                    return item
                })
            )
        };
        setTask({title: '', priority: ''})
        setEditID(null)
        setIsEditing(false)
        alert("success")

        const newItem = {id: new Date().getTime().toString(), task}
        setList([...list, newItem])
        setTask({title: '', priority: ''})

    }
    return (
        <>
            <main className='section-center'>
                <Add handleChange={handleChange} handleSubmit={handleSubmit} task={task}/>
                <section>
                     <h4>To Do List</h4>
                    <div className='form-control'>
                        <input type="search" className='search' placeholder='Search...' />
                    </div>
                </section>
               <List list={list}/>
            </main>
        </>
    )

}

export default App;
